#!/usr/bin/env tsx
/*
 * Migrate existing passkeys from vault to SQLite DB.
 */
import { vaultManager } from '../src/server/vault';
import path from 'path';
import openSqlite from '../src/lib/sqlite-driver';

let args = process.argv.slice(2);
let dbPath = path.resolve(process.cwd(), 'data', 'credentials.db');
if (args.length && (args[0].includes('/') || args[0].includes('\\') || args[0].endsWith('.db'))) {
  dbPath = args[0];
  args = args.slice(1);
}
const db = openSqlite(dbPath);

async function ensureSchema() {
  await db.run(`CREATE TABLE IF NOT EXISTS credentials (
    id TEXT NOT NULL,
    credential_id TEXT NOT NULL,
    public_key TEXT,
    counter INTEGER DEFAULT 0,
    created_at TEXT,
    label TEXT,
    PRIMARY KEY(id, credential_id)
  )`);
}

function insert(id: string, cred: any) {
  return db.run('INSERT OR REPLACE INTO credentials (id, credential_id, public_key, counter, created_at, label) VALUES (?, ?, ?, ?, ?, ?)', [
    id,
    cred.credentialID || cred.id,
    cred.credentialPublicKey || cred.publicKey,
    cred.counter ?? 0,
    cred.createdAt ?? new Date().toISOString(),
    cred.label ?? null
  ]);
}

(async () => {
  await ensureSchema();
  // Warning: this uses the vault API, which may be keytar or memory.
  // Iterate all ids - for vault we don't have an index, so accept `ids` as argv
  const ids = args;
  if (ids.length === 0) {
    console.error('usage: migrate-vault-to-sqlite.ts <id> <id>... (dbPath optional)');
    process.exit(1);
  }
  // if first arg looks like a path and exists as file, use it
  const maybePath = ids[0];
  if (maybePath.includes('/') || maybePath.includes('\\')) {
    // treat as db path
    // shift
  }
  for (const id of ids) {
    const s = await vaultManager.getSecret(`passkey:${id}`);
    if (!s) continue;
    try {
      const creds = JSON.parse(s);
      for (const c of creds) await insert(id, c);
      console.log(`Migrated ${creds.length} credentials for ${id}`);
      // Optionally clear the vault: vaultManager.deleteSecret(`passkey:${id}`)
    } catch (e) {
      console.error('Invalid creds for', id, e);
    }
  }
  process.exit(0);
})();

/**
 * Login / Key selection UI used to open a session by decrypting a stored
 * API key and verifying it with the server.
 */
import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';

import { storageManager } from '@/lib/storage';
import { useCloudflareAPI } from '@/hooks/use-cloudflare-api';
import { useToast } from '@/hooks/use-toast';
import { storageBackend } from '@/lib/storage-util';
import { useTranslation } from 'react-i18next';
import { Key, Trash2, Pencil } from 'lucide-react';
import { cryptoManager } from '@/lib/crypto';
import { AddKeyDialog } from './AddKeyDialog';
import PasskeyManagerDialog from './PasskeyManagerDialog';
import { ServerClient } from '@/lib/server-client';
import { EncryptionSettingsDialog } from './EncryptionSettingsDialog';
import { EditKeyDialog } from './EditKeyDialog';
import type { ApiKey } from '@/types/dns';

/**
 * Props for the login form used on the main page to select and decrypt
 * an API key and authenticate the user.
 */
interface LoginFormProps {
  /** Callback invoked on successful login with the decrypted apiKey */
  onLogin: (apiKey: string) => void;
}

/**
 * Login form component responsible for decrypting stored API keys and
 * verifying them with the server. On success the decrypted key is passed
 * via `onLogin` for the parent to use.
 */
export function LoginForm({ onLogin }: LoginFormProps) {
  const [selectedKeyId, setSelectedKeyId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showAddKey, setShowAddKey] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [newKeyLabel, setNewKeyLabel] = useState('');
  const [newApiKey, setNewApiKey] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showEditKey, setShowEditKey] = useState(false);
  const [editingKeyId, setEditingKeyId] = useState('');
  const [editLabel, setEditLabel] = useState('');
  const [editEmail, setEditEmail] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [editPassword, setEditPassword] = useState('');
  const [encryptionSettings, setEncryptionSettings] = useState(cryptoManager.getConfig());
  const [benchmarkResult, setBenchmarkResult] = useState<number | null>(null);
  const [passkeyRegisterLoading, setPasskeyRegisterLoading] = useState(false);
  const [passkeyAuthLoading, setPasskeyAuthLoading] = useState(false);
  const [showManagePasskeys, setShowManagePasskeys] = useState(false);
  const [passkeyViewKey, setPasskeyViewKey] = useState('');
  const [passkeyViewEmail, setPasskeyViewEmail] = useState<string | undefined>(undefined);
  
  const { toast } = useToast();
  const { verifyToken } = useCloudflareAPI();
  const [apiKeys, setApiKeys] = useState(storageManager.getApiKeys());
  const backend = storageBackend();
  const { t } = useTranslation();
  useEffect(() => {
    if (backend !== 'indexeddb') {
      toast({
        title: t('Note'),
        description: `Using ${backend} storage. IndexedDB not available or in use. Some features may be limited.`,
      });
    }
  }, [backend, toast]);

  useEffect(() => {
    cryptoManager.reloadConfig();
    setEncryptionSettings(cryptoManager.getConfig());
  }, []);

  const handleLogin = async () => {
    if (!selectedKeyId || !password) {
      toast({
        title: "Error",
        description: "Please select an API key and enter your password",
        variant: "destructive"
      });
      return;
    }

    setIsLoading(true);
    try {
      const decrypted = await storageManager.getDecryptedApiKey(selectedKeyId, password);
      const decryptedKey = decrypted?.key;
      const email = decrypted?.email;
      if (!decryptedKey) {
        toast({
          title: "Error",
          description: "Invalid password or corrupted key",
          variant: "destructive"
        });
        return;
      }

      // Verify the API key works
      try {
        await verifyToken(decryptedKey, email);
      } catch (err) {
        toast({
          title: "Error",
          description: (err as Error).message,
          variant: "destructive"
        });
        return;
      }

      storageManager.setCurrentSession(selectedKeyId);
      onLogin(decryptedKey);
      
      toast({
        title: "Success",
        description: "Logged in successfully"
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to login: " + (error as Error).message,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddKey = async () => {
    if (!newKeyLabel || !newApiKey || !newPassword) {
      toast({
        title: "Error",
        description: "Please fill in all fields",
        variant: "destructive"
      });
      return;
    }

    try {
      // Test the API key first
      try {
        await verifyToken(newApiKey, newEmail || undefined);
      } catch (err) {
        toast({
          title: "Error",
          description: (err as Error).message,
          variant: "destructive"
        });
        return;
      }

      await storageManager.addApiKey(newKeyLabel, newApiKey, newPassword, newEmail || undefined);
      setApiKeys(storageManager.getApiKeys());
      setNewKeyLabel('');
      setNewApiKey('');
      setNewEmail('');
      setNewPassword('');
      setShowAddKey(false);
      
      toast({
        title: "Success",
        description: "API key added successfully"
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add API key: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleRegisterPasskey = async () => {
    if (!selectedKeyId) {
      toast({ title: 'Error', description: 'Select a key before registering a passkey', variant: 'destructive' });
      return;
    }

    if (!password) {
      toast({ title: 'Error', description: 'Enter your password to decrypt the key for registration', variant: 'destructive' });
      return;
    }

    setPasskeyRegisterLoading(true);
    try {
      const decrypted = await storageManager.getDecryptedApiKey(selectedKeyId, password);
      if (!decrypted?.key) {
        toast({ title: 'Error', description: 'Invalid password or corrupted key', variant: 'destructive' });
        return;
      }

      try {
        const sc = new ServerClient(decrypted.key, undefined, decrypted.email);
        await sc.storeVaultSecret(selectedKeyId, decrypted.key);
      } catch (err) {
        toast({ title: 'Warning', description: 'Failed to store key to OS vault: ' + (err as Error).message });
      }
      const sc2 = new ServerClient(decrypted.key, undefined, decrypted.email);
      const options = await sc2.getPasskeyRegistrationOptions(selectedKeyId);
      const challenge = Uint8Array.from(atob(options.challenge), c => c.charCodeAt(0));
      const publicKey = {
        challenge,
        rp: { name: 'Better Cloudflare' },
        user: { id: Uint8Array.from(new TextEncoder().encode(selectedKeyId)), name: selectedKeyId, displayName: selectedKeyId },
        pubKeyCredParams: [{ alg: -7, type: 'public-key' }],
      } as any;
      const credential = await navigator.credentials.create({ publicKey });
      if (credential) {
        const att = credential as PublicKeyCredential;
        const attObj = {
          id: att.id,
          rawId: Array.from(new Uint8Array(att.rawId)),
          response: {
            clientDataJSON: Array.from(new Uint8Array(att.response.clientDataJSON)),
            attestationObject: Array.from(new Uint8Array((att.response as any).attestationObject || new ArrayBuffer(0))),
          },
        };
        await sc2.registerPasskey(selectedKeyId, attObj);
        toast({ title: 'Success', description: 'Passkey registered to this key' });
      }
    } catch (error) {
      toast({ title: 'Error', description: 'Passkey registration failed: ' + (error as Error).message, variant: 'destructive' });
    } finally {
      setPasskeyRegisterLoading(false);
    }
  };

  const handleUsePasskey = async () => {
    if (!selectedKeyId) {
      toast({ title: 'Error', description: 'Select a key to authenticate', variant: 'destructive' });
      return;
    }
    setPasskeyAuthLoading(true);
    try {
      const scx = new ServerClient('', undefined);
      const opts = await scx.getPasskeyAuthOptions(selectedKeyId);
      const challenge = Uint8Array.from(atob(opts.challenge), c => c.charCodeAt(0));
      const publicKey = {
        challenge,
        allowCredentials: [],
      } as any;
      const assertion = await navigator.credentials.get({ publicKey });
      if (assertion) {
        const a = assertion as PublicKeyCredential;
        const auth = {
          id: a.id,
          rawId: Array.from(new Uint8Array(a.rawId)),
          response: {
            clientDataJSON: Array.from(new Uint8Array(a.response.clientDataJSON)),
            authenticatorData: Array.from(new Uint8Array((a.response as any).authenticatorData || new ArrayBuffer(0))),
            signature: Array.from(new Uint8Array((a.response as any).signature || new ArrayBuffer(0))),
            userHandle: (a.response as any).userHandle ? Array.from(new Uint8Array((a.response as any).userHandle)) : null,
          },
        };
        const serverResp = await scx.authenticatePasskey(selectedKeyId, auth);
        if ((serverResp as any).success) {
          const secret = await scx.getVaultSecret(selectedKeyId);
          if (secret) {
            storageManager.setCurrentSession(selectedKeyId);
            onLogin(secret);
            toast({ title: 'Success', description: 'Logged in using passkey' });
          } else {
            toast({ title: 'Error', description: 'No secret in vault for this key', variant: 'destructive' });
          }
        } else {
          toast({ title: 'Error', description: 'Passkey authentication failed', variant: 'destructive' });
        }
      }
    } catch (error) {
      toast({ title: 'Error', description: 'Passkey auth failed: ' + (error as Error).message, variant: 'destructive' });
    } finally {
      setPasskeyAuthLoading(false);
    }
  };

  const handleEditKeyInit = (key: ApiKey) => {
    setEditingKeyId(key.id);
    setEditLabel(key.label);
    setEditEmail(key.email || '');
    setCurrentPassword('');
    setEditPassword('');
    setShowEditKey(true);
  };

  const handleUpdateKey = async () => {
    if (!editingKeyId) return;
    try {
      await storageManager.updateApiKey(editingKeyId, {
        label: editLabel,
        email: editEmail || undefined,
        currentPassword: currentPassword || undefined,
        newPassword: editPassword || undefined,
      });
      setApiKeys(storageManager.getApiKeys());
      setShowEditKey(false);
      toast({
        title: 'Success',
        description: 'API key updated successfully',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to update API key: ' + (error as Error).message,
        variant: 'destructive',
      });
    }
  };

  const handleDeleteKey = (keyId: string) => {
    storageManager.removeApiKey(keyId);
    setApiKeys(storageManager.getApiKeys());
    if (selectedKeyId === keyId) {
      setSelectedKeyId('');
    }
    toast({
      title: "Success",
      description: "API key deleted"
    });
  };

  const handleBenchmark = async () => {
    try {
      const { benchmark } = await import('@/lib/crypto-benchmark');
      const result = await benchmark(encryptionSettings.iterations);
      setBenchmarkResult(result);
      toast({
        title: "Benchmark Complete",
        description: `Encryption took ${result.toFixed(2)}ms`
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Benchmark failed: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleUpdateSettings = () => {
    cryptoManager.updateConfig(encryptionSettings);
    toast({
      title: "Success",
      description: "Encryption settings updated"
    });
    setShowSettings(false);
  };

  const [vaultEnabled, setVaultEnabled] = useState(storageManager.getVaultEnabled());

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <div className="p-3 bg-primary/10 rounded-full">
              <Key className="h-8 w-8 text-primary" />
            </div>
          </div>
          <CardTitle className="text-2xl">{t('Cloudflare DNS Manager')}</CardTitle>
          <CardDescription>
            {t('Select your API key and enter your password to continue')}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="api-key">{t('API Key')}</Label>
            <Select value={selectedKeyId} onValueChange={setSelectedKeyId}>
              <SelectTrigger>
              <SelectValue placeholder={t('Select an API key')} />
              </SelectTrigger>
              <SelectContent>
                {apiKeys.map((key) => (
                  <SelectItem key={key.id} value={key.id}>
                    <div className="flex items-center justify-between w-full">
                      <span>{key.label}</span>
                      <div className="flex">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEditKeyInit(key);
                          }}
                          className="h-6 w-6 p-0 ml-2"
                        >
                          <Pencil className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteKey(key.id);
                          }}
                          className="h-6 w-6 p-0 ml-2"
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
              <Label htmlFor="password">{t('Password')}</Label>
            <Input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
                  placeholder={t('Enter your password')}
              onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
            />
          </div>

          <Button 
            onClick={handleLogin} 
            className="w-full" 
            disabled={isLoading || !selectedKeyId || !password}
          >
            {isLoading ? t('Logging in...') : t('Login')}
          </Button>

          <div className="flex gap-2">
          <AddKeyDialog
            open={showAddKey}
            onOpenChange={setShowAddKey}
            label={newKeyLabel}
            onLabelChange={setNewKeyLabel}
            apiKey={newApiKey}
            onApiKeyChange={setNewApiKey}
            email={newEmail}
            onEmailChange={setNewEmail}
            password={newPassword}
            onPasswordChange={setNewPassword}
            onAdd={handleAddKey}
          />

            <EncryptionSettingsDialog
              open={showSettings}
              onOpenChange={setShowSettings}
              settings={encryptionSettings}
              onSettingsChange={setEncryptionSettings}
              onBenchmark={handleBenchmark}
              onUpdate={handleUpdateSettings}
              benchmarkResult={benchmarkResult}
              vaultEnabled={vaultEnabled}
              onVaultEnabledChange={(enabled: boolean) => {
                setVaultEnabled(enabled);
                storageManager.setVaultEnabled(enabled);
              }}
            />
            <Button
              variant="outline"
              size="sm"
              onClick={handleRegisterPasskey}
              disabled={!selectedKeyId || !password || passkeyRegisterLoading}
            >
              {passkeyRegisterLoading ? 'Registering...' : 'Register Passkey'}
            </Button>
            <PasskeyManagerDialog
              open={showManagePasskeys}
              onOpenChange={(open: boolean) => {
                if (!open) {
                  setShowManagePasskeys(false);
                  setPasskeyViewKey('');
                  setPasskeyViewEmail(undefined);
                } else {
                  setShowManagePasskeys(true);
                }
              }}
              id={selectedKeyId}
              apiKey={passkeyViewKey}
              email={passkeyViewEmail}
            />
            <Button
              variant="outline"
              size="sm"
              onClick={async () => {
                if (!selectedKeyId || !password) return;
                try {
                  const decrypted = await storageManager.getDecryptedApiKey(selectedKeyId, password);
                  if (!decrypted?.key) {
                    toast({ title: 'Error', description: 'Invalid password', variant: 'destructive' });
                    return;
                  }
                  setPasskeyViewKey(decrypted.key);
                  setPasskeyViewEmail(decrypted.email);
                  setShowManagePasskeys(true);
                } catch (err) {
                  toast({ title: 'Error', description: 'Failed to decrypt key: ' + (err as Error).message, variant: 'destructive' });
                }
              }}
              disabled={!selectedKeyId || !password}
            >
              Manage Passkeys
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={handleUsePasskey}
              disabled={!selectedKeyId || passkeyAuthLoading}
            >
              {passkeyAuthLoading ? 'Authenticating...' : 'Use Passkey'}
            </Button>
            {vaultEnabled && (
              <Button
                variant="destructive"
                size="sm"
                onClick={async () => {
                  if (!selectedKeyId || !password) {
                    toast({ title: t('Error'), description: t('Select key and enter password to delete vault secret'), variant: 'destructive' });
                    return;
                  }
                  try {
                    const dec = await storageManager.getDecryptedApiKey(selectedKeyId, password);
                    if (!dec?.key) {
                      toast({ title: t('Error'), description: t('Invalid password'), variant: 'destructive' });
                      return;
                    }
                    const sc = new ServerClient(dec.key, undefined, dec.email);
                    await sc.deleteVaultSecret(selectedKeyId);
                    toast({ title: t('Success'), description: t('Vault secret removed') });
                  } catch (err) {
                    toast({ title: t('Error'), description: t('Failed to remove vault secret: ') + (err as Error).message, variant: 'destructive' });
                  }
                }}
              >
                Remove Vault Secret
              </Button>
            )}
          </div>
          <EditKeyDialog
            open={showEditKey}
            onOpenChange={setShowEditKey}
            label={editLabel}
            onLabelChange={setEditLabel}
            email={editEmail}
            onEmailChange={setEditEmail}
            currentPassword={currentPassword}
            onCurrentPasswordChange={setCurrentPassword}
            newPassword={editPassword}
            onNewPasswordChange={setEditPassword}
            onSave={handleUpdateKey}
          />
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Dialog used to collect DNS record properties required to create a new
 * record via the API.
 */
import type { ChangeEvent } from 'react';
import { useEffect, useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import type { DNSRecord, RecordType, TTLValue } from '@/types/dns';
import { parseSPF, composeSPF, validateSPF } from '@/lib/spf';
import type { SPFGraph, SPFMechanism } from '@/lib/spf';
import { useCloudflareAPI } from '@/hooks/use-cloudflare-api';
import { RECORD_TYPES, getTTLPresets, getRecordTypeLabel } from '@/types/dns';
import { Plus } from 'lucide-react';

/**
 * Props for the AddRecordDialog component which collects fields to create a
 * new DNS record (type, name, content, ttl, etc.).
 */
export interface AddRecordDialogProps {
  /** Whether the dialog is currently open */
  open: boolean;
  /** Callback invoked when open state changes (open/close) */
  onOpenChange: (open: boolean) => void;
  /** Working DNS record object for the form */
  record: Partial<DNSRecord>;
  /** Called when fields in the form change with the updated record */
  onRecordChange: (record: Partial<DNSRecord>) => void;
  /** Called to create the new record */
  onAdd: () => void;
  /** Optional name of the zone to display in the dialog */
  zoneName?: string;
  apiKey?: string;
  email?: string;
}

/**
 * Dialog that collects fields to create a DNS record and forwards the
 * create action via `onAdd`.
 */
export function AddRecordDialog({ open, onOpenChange, record, onRecordChange, onAdd, zoneName, apiKey, email }: AddRecordDialogProps) {
  const { simulateSPF, getSPFGraph } = useCloudflareAPI(apiKey, email);
  const ttlValue = record.ttl === 1 ? 'auto' : record.ttl;
  const isCustomTTL = ttlValue !== undefined && !getTTLPresets().includes(ttlValue as TTLValue);
  // SRV fields (priority weight port target) parsed from record.content
  const parseSRV = useCallback((content?: string) => {
    if (!content) return { priority: undefined, weight: undefined, port: undefined, target: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 4) return { priority: undefined, weight: undefined, port: undefined, target: content };
    const [priority, weight, port, ...rest] = parts;
    return { priority: Number(priority), weight: Number(weight), port: Number(port), target: rest.join(' ') };
  }, [] as const);
  const composeSRV = (p?: number, w?: number, prt?: number, t?: string) => `${p ?? 0} ${w ?? 0} ${prt ?? 0} ${t ?? ''}`;
  const [srvPriority, setSrvPriority] = useState<number | undefined>(parseSRV(record.content).priority);
  const [srvWeight, setSrvWeight] = useState<number | undefined>(parseSRV(record.content).weight);
  const [srvPort, setSrvPort] = useState<number | undefined>(parseSRV(record.content).port);
  const [srvTarget, setSrvTarget] = useState<string>(parseSRV(record.content).target ?? '');
  // TLSA fields (usage selector matchingType data)
  const parseTLSA = useCallback((content?: string) => {
    if (!content) return { usage: undefined, selector: undefined, matchingType: undefined, data: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 4) return { usage: undefined, selector: undefined, matchingType: undefined, data: content };
    const [usage, selector, matchingType, ...rest] = parts;
    return { usage: Number(usage), selector: Number(selector), matchingType: Number(matchingType), data: rest.join(' ') };
  }, [] as const);
  const composeTLSA = (u?: number, s?: number, m?: number, d?: string) => `${u ?? 0} ${s ?? 0} ${m ?? 0} ${d ?? ''}`;
  const [tlsaUsage, setTlsaUsage] = useState<number | undefined>(parseTLSA(record.content).usage);
  const [tlsaSelector, setTlsaSelector] = useState<number | undefined>(parseTLSA(record.content).selector);
  const [tlsaMatchingType, setTlsaMatchingType] = useState<number | undefined>(parseTLSA(record.content).matchingType);
  const [tlsaData, setTlsaData] = useState<string>(parseTLSA(record.content).data ?? '');
  // SSHFP fields (algorithm, fptype, fingerprint)
  const parseSSHFP = useCallback((content?: string) => {
    if (!content) return { algorithm: undefined, fptype: undefined, fingerprint: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 3) return { algorithm: undefined, fptype: undefined, fingerprint: content };
    const [algorithm, fptype, ...rest] = parts;
    return { algorithm: Number(algorithm), fptype: Number(fptype), fingerprint: rest.join(' ') };
  }, [] as const);
  const composeSSHFP = (a?: number, f?: number, fp?: string) => `${a ?? 0} ${f ?? 0} ${fp ?? ''}`;
  const [sshfpAlgorithm, setSshfpAlgorithm] = useState<number | undefined>(parseSSHFP(record.content).algorithm);
  const [sshfpFptype, setSshfpFptype] = useState<number | undefined>(parseSSHFP(record.content).fptype);
  const [sshfpFingerprint, setSshfpFingerprint] = useState<string>(parseSSHFP(record.content).fingerprint ?? '');
  const splitNaptrTokens = useCallback((s: string) => {
    const tokens: string[] = [];
    let current = '';
    let inQuote = false;
    for (let i = 0; i < s.length; i++) {
      const ch = s[i];
      if (ch === '"') {
        inQuote = !inQuote;
        current += ch;
        continue;
      }
      if (ch === ' ' && !inQuote) {
        if (current.trim().length > 0) {
          tokens.push(current.trim());
          current = '';
        }
        continue;
      }
      current += ch;
    }
    if (current.trim().length > 0) tokens.push(current.trim());
    return tokens;
  }, [] as const);

  const parseNAPTR = useCallback((content?: string) => {
    if (!content) return { order: undefined, preference: undefined, flags: '', service: '', regexp: '', replacement: '' };
    const tokens = splitNaptrTokens(String(content).trim());
    const [order, preference, flags, service, regexp, replacement] = tokens;
    return { order: Number(order), preference: Number(preference), flags: flags?.replace(/^"|"$/g, ''), service, regexp: regexp?.replace(/^"|"$/g, ''), replacement };
  }, [splitNaptrTokens] as const);
  const quoteIfNeeded = (s?: string) => {
    if (!s) return '';
    if (/\s/.test(s) || /"/.test(s)) return `"${s.replace(/"/g, '\\"')}"`;
    return s;
  };
  const composeNAPTR = (o?: number, p?: number, f?: string, s?: string, r?: string, rep?: string) => `${o ?? 0} ${p ?? 0} ${f ?? ''} ${s ?? ''} ${quoteIfNeeded(r)} ${rep ?? ''}`;
  const [naptrOrder, setNaptrOrder] = useState<number | undefined>(parseNAPTR(record.content).order);
  const [naptrPref, setNaptrPref] = useState<number | undefined>(parseNAPTR(record.content).preference);
  const [naptrFlags, setNaptrFlags] = useState<string>(parseNAPTR(record.content).flags ?? '');
  const [naptrService, setNaptrService] = useState<string>(parseNAPTR(record.content).service ?? '');
  const [naptrRegexp, setNaptrRegexp] = useState<string>(parseNAPTR(record.content).regexp ?? '');
  const [naptrReplacement, setNaptrReplacement] = useState<string>(parseNAPTR(record.content).replacement ?? '');

  useEffect(() => {
    if (record.type === 'SRV') {
      const parsed = parseSRV(record.content);
      setSrvPriority(parsed.priority);
      setSrvWeight(parsed.weight);
      setSrvPort(parsed.port);
      setSrvTarget(parsed.target ?? '');
    }
  }, [record.type, record.content, parseNAPTR, parseSSHFP, parseTLSA, parseSRV]);
  useEffect(() => {
    if (record.type === 'TLSA') {
      const parsed = parseTLSA(record.content);
      setTlsaUsage(parsed.usage);
      setTlsaSelector(parsed.selector);
      setTlsaMatchingType(parsed.matchingType);
      setTlsaData(parsed.data ?? '');
    }
    if (record.type === 'SSHFP') {
      const parsed = parseSSHFP(record.content);
      setSshfpAlgorithm(parsed.algorithm);
      setSshfpFptype(parsed.fptype);
      setSshfpFingerprint(parsed.fingerprint ?? '');
    }
    if (record.type === 'NAPTR') {
      const parsed = parseNAPTR(record.content);
      setNaptrOrder(parsed.order);
      setNaptrPref(parsed.preference);
      setNaptrFlags(parsed.flags ?? '');
      setNaptrService(parsed.service ?? '');
      setNaptrRegexp(parsed.regexp ?? '');
      setNaptrReplacement(parsed.replacement ?? '');
    }
    // SSHFP state managed similarly below
  }, [record.type, record.content]);

  // SPF state and builder
  const parsedSPF = parseSPF(record.content);
  const [newSPFQualifier, setNewSPFQualifier] = useState<string>('');
  const [newSPFMechanism, setNewSPFMechanism] = useState<string>('ip4');
  const [newSPFValue, setNewSPFValue] = useState<string>('');
  const [editingSPFIndex, setEditingSPFIndex] = useState<number | null>(null);
  const [spfSimIp, setSpfSimIp] = useState<string>('');
  const [spfSimResult, setSpfSimResult] = useState<{ result: string; reasons: string[]; lookups: number } | null>(null);
  const [spfGraph, setSpfGraph] = useState<SPFGraph | null>(null);
  const [spfGraphError, setSpfGraphError] = useState<string | null>(null);

  const addSPFMechanism = () => {
    const mechVal = newSPFValue?.trim();
    const newMech: SPFMechanism = { qualifier: newSPFQualifier || undefined, mechanism: newSPFMechanism as unknown as string, value: mechVal || undefined } as SPFMechanism;
    const parsed = parseSPF(record.content) ?? { version: 'v=spf1', mechanisms: [] };
    const mechs = [...parsed.mechanisms];
    if (editingSPFIndex !== null && editingSPFIndex >= 0 && editingSPFIndex < mechs.length) {
      mechs[editingSPFIndex] = newMech;
      setEditingSPFIndex(null);
    } else {
      mechs.push(newMech);
    }
    const updated = composeSPF({ version: parsed.version, mechanisms: mechs as SPFMechanism[] });
    onRecordChange({ ...record, content: updated });
    // reset form
    setNewSPFValue('');
    setNewSPFQualifier('');
    setNewSPFMechanism('ip4');
  };

  const removeSPFMechanism = (index: number) => {
    const parsed = parseSPF(record.content) ?? { version: 'v=spf1', mechanisms: [] };
    const mechs = [...parsed.mechanisms];
    mechs.splice(index, 1);
    const updated = composeSPF({ version: parsed.version, mechanisms: mechs as SPFMechanism[] });
    onRecordChange({ ...record, content: updated });
  };

  const editSPFMechanism = (index: number) => {
    const parsed = parseSPF(record.content) ?? { version: 'v=spf1', mechanisms: [] };
    const m = parsed.mechanisms[index];
    setNewSPFQualifier(m.qualifier || '');
    setNewSPFMechanism(m.mechanism || 'ip4');
    setNewSPFValue(m.value || '');
    setEditingSPFIndex(index);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogTrigger asChild>
        <Button>
          <Plus className="h-4 w-4 mr-2" />
          Add Record
        </Button>
      </DialogTrigger>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>Add DNS Record</DialogTitle>
          <DialogDescription>
            Create a new DNS record for {zoneName}
          </DialogDescription>
        </DialogHeader>
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label>Type</Label>
              <Select
                value={record.type}
                onValueChange={(value: string) =>
                  onRecordChange({
                    ...record,
                    type: value as RecordType,
                    priority: value === 'MX' ? record.priority : undefined
                  })
                }
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {RECORD_TYPES.map((type) => (
                    <SelectItem key={type} value={type} title={getRecordTypeLabel(type)}>
                      {getRecordTypeLabel(type)}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>TTL</Label>
              <Select
                value={isCustomTTL ? 'custom' : String(ttlValue)}
                onValueChange={(value: string) => {
                  if (value === 'custom') {
                    onRecordChange({ ...record, ttl: 300 });
                  } else {
                    onRecordChange({
                      ...record,
                      ttl: value === 'auto' ? 'auto' : Number(value)
                    });
                  }
                }}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {getTTLPresets().map((ttl) => (
                    <SelectItem key={ttl} value={String(ttl)}>
                      {ttl === 'auto' ? 'Auto' : ttl}
                    </SelectItem>
                  ))}
                  <SelectItem value="custom">Custom</SelectItem>
                </SelectContent>
              </Select>
              {isCustomTTL && (
                <Input
                  type="number"
                  value={typeof record.ttl === 'number' ? record.ttl : ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    onRecordChange({
                      ...record,
                      ttl: Number.isNaN(n) ? 300 : n
                    });
                  }}
                />
              )}
            </div>
          </div>
          <div className="space-y-2">
            <Label>Name</Label>
            <Input
              value={record.name}
              onChange={(e: ChangeEvent<HTMLInputElement>) => onRecordChange({
                ...record,
                name: e.target.value
              })}
              placeholder="e.g., www or @ for root"
            />
          </div>
          <div className="space-y-2">
            <Label>Content</Label>
            {(() => {
              switch (record.type) {
                case 'SRV':
                  return (
                    <div className="grid grid-cols-4 gap-2">
                      <Input
                        type="number"
                        placeholder="priority"
                        value={srvPriority ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          setSrvPriority(Number.isNaN(n) ? undefined : n);
                          onRecordChange({ ...record, content: composeSRV(Number.isNaN(n) ? undefined : n, srvWeight, srvPort, srvTarget) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="weight"
                        value={srvWeight ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          setSrvWeight(Number.isNaN(n) ? undefined : n);
                          onRecordChange({ ...record, content: composeSRV(srvPriority, Number.isNaN(n) ? undefined : n, srvPort, srvTarget) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="port"
                        value={srvPort ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          setSrvPort(Number.isNaN(n) ? undefined : n);
                          onRecordChange({ ...record, content: composeSRV(srvPriority, srvWeight, Number.isNaN(n) ? undefined : n, srvTarget) });
                        }}
                      />
                      <Input
                        placeholder="target"
                        value={srvTarget}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setSrvTarget(e.target.value);
                          onRecordChange({ ...record, content: composeSRV(srvPriority, srvWeight, srvPort, e.target.value) });
                        }}
                      />
                    </div>
                  );
                case 'TLSA':
                  return (
                    <div className="grid grid-cols-4 gap-2">
                      <Input
                        type="number"
                        placeholder="usage"
                        value={tlsaUsage ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setTlsaUsage(val);
                          onRecordChange({ ...record, content: composeTLSA(val, tlsaSelector, tlsaMatchingType, tlsaData) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="selector"
                        value={tlsaSelector ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setTlsaSelector(val);
                          onRecordChange({ ...record, content: composeTLSA(tlsaUsage, val, tlsaMatchingType, tlsaData) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="matching type"
                        value={tlsaMatchingType ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setTlsaMatchingType(val);
                          onRecordChange({ ...record, content: composeTLSA(tlsaUsage, tlsaSelector, val, tlsaData) });
                        }}
                      />
                      <Input
                        placeholder="data"
                        value={tlsaData}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setTlsaData(e.target.value);
                          onRecordChange({ ...record, content: composeTLSA(tlsaUsage, tlsaSelector, tlsaMatchingType, e.target.value) });
                        }}
                      />
                    </div>
                  );
                case 'SSHFP':
                  return (
                    <div className="grid grid-cols-3 gap-2">
                      <Input
                        type="number"
                        placeholder="algorithm"
                        value={sshfpAlgorithm ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setSshfpAlgorithm(val);
                          onRecordChange({ ...record, content: composeSSHFP(val, sshfpFptype, sshfpFingerprint) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="fptype"
                        value={sshfpFptype ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setSshfpFptype(val);
                          onRecordChange({ ...record, content: composeSSHFP(sshfpAlgorithm, val, sshfpFingerprint) });
                        }}
                      />
                      <Input
                        placeholder="fingerprint"
                        value={sshfpFingerprint}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setSshfpFingerprint(e.target.value);
                          onRecordChange({ ...record, content: composeSSHFP(sshfpAlgorithm, sshfpFptype, e.target.value) });
                        }}
                      />
                    </div>
                  );
                case 'NAPTR':
                  return (
                    <div className="grid grid-cols-6 gap-2">
                      <Input
                        type="number"
                        placeholder="order"
                        value={naptrOrder ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setNaptrOrder(val);
                          onRecordChange({ ...record, content: composeNAPTR(val, naptrPref, naptrFlags, naptrService, naptrRegexp, naptrReplacement) });
                        }}
                      />
                      <Input
                        type="number"
                        placeholder="preference"
                        value={naptrPref ?? ''}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          const n = Number.parseInt(e.target.value, 10);
                          const val = Number.isNaN(n) ? undefined : n;
                          setNaptrPref(val);
                          onRecordChange({ ...record, content: composeNAPTR(naptrOrder, val, naptrFlags, naptrService, naptrRegexp, naptrReplacement) });
                        }}
                      />
                      <Input
                        placeholder="flags"
                        value={naptrFlags}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setNaptrFlags(e.target.value);
                          onRecordChange({ ...record, content: composeNAPTR(naptrOrder, naptrPref, e.target.value, naptrService, naptrRegexp, naptrReplacement) });
                        }}
                      />
                      <Input
                        placeholder="service"
                        value={naptrService}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setNaptrService(e.target.value);
                          onRecordChange({ ...record, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, e.target.value, naptrRegexp, naptrReplacement) });
                        }}
                      />
                      <Input
                        placeholder="regexp"
                        value={naptrRegexp}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setNaptrRegexp(e.target.value);
                          onRecordChange({ ...record, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, naptrService, e.target.value, naptrReplacement) });
                        }}
                      />
                      <Input
                        placeholder="replacement"
                        value={naptrReplacement}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => {
                          setNaptrReplacement(e.target.value);
                          onRecordChange({ ...record, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, naptrService, naptrRegexp, e.target.value) });
                        }}
                      />
                    </div>
                  );
                case 'SPF':
                  return (
                    <div className="space-y-2">
                      <Input
                        value={record.content}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => onRecordChange({
                          ...record,
                          content: e.target.value
                        })}
                        placeholder="v=spf1 ip4:... ~all"
                      />
                      <div className="flex space-x-2">
                        <select className="h-8 p-2" value={newSPFQualifier} onChange={(e) => setNewSPFQualifier(e.target.value)}>
                          <option value="">+</option>
                          <option value="-">-</option>
                          <option value="~">~</option>
                          <option value="?">?</option>
                        </select>
                        <select className="h-8 p-2" value={newSPFMechanism} onChange={(e) => setNewSPFMechanism(e.target.value)}>
                          <option value="ip4">ip4</option>
                          <option value="ip6">ip6</option>
                          <option value="a">a</option>
                          <option value="mx">mx</option>
                          <option value="include">include</option>
                          <option value="all">all</option>
                        </select>
                        <Input placeholder="value (optional)" value={newSPFValue} onChange={(e: ChangeEvent<HTMLInputElement>) => setNewSPFValue(e.target.value)} />
                        <Button onClick={addSPFMechanism}>{editingSPFIndex !== null ? 'Update' : 'Add'}</Button>
                      </div>
                      <div className="text-sm text-muted">
                        {parsedSPF?.mechanisms && parsedSPF.mechanisms.length > 0 && (
                          <div className="space-y-1 mb-2">
                            <div className="text-xs font-semibold">Mechanisms:</div>
                            {parsedSPF.mechanisms.map((m, i) => (
                              <div key={`${m.mechanism}:${i}`} className="flex items-center space-x-2">
                                <div className="text-xs">{`${m.qualifier ?? ''}${m.mechanism}${m.value ? `:${m.value}` : ''}`}{editingSPFIndex===i ? ' (editing)' : ''}</div>
                                <Button size="sm" onClick={() => editSPFMechanism(i)}>Edit</Button>
                                <Button size="sm" variant="destructive" onClick={() => removeSPFMechanism(i)}>Remove</Button>
                              </div>
                            ))}
                          </div>
                        )}
                        <div>Preview:</div>
                        <pre className="whitespace-pre-wrap">{composeSPF(parsedSPF ?? { version: 'v=spf1', mechanisms: [] })}</pre>
                        {!validateSPF(record.content).ok && (
                          <div className="text-red-600">SPF validation issues: {validateSPF(record.content).problems.join(', ')}</div>
                        )}
                        <div className="flex space-x-2 mt-2">
                          <Input placeholder="simulate IP e.g., 1.2.3.4" value={spfSimIp} onChange={(e) => setSpfSimIp(e.target.value)} />
                          <Button onClick={async () => {
                            if (!zoneName) return;
                            const domain = (record.name === '@' || !record.name) ? zoneName : `${record.name}.${zoneName}`;
                            try {
                              const result = await simulateSPF(domain, spfSimIp);
                              setSpfSimResult(result);
                            } catch (err) {
                              setSpfSimResult({ result: 'temperror', reasons: [(err as Error).message], lookups: 0 });
                            }
                          }}>Simulate</Button>
                          <Button onClick={async () => {
                            if (!zoneName) return;
                            const domain = (record.name === '@' || !record.name) ? zoneName : `${record.name}.${zoneName}`;
                            try {
                              const graph = await getSPFGraph(domain);
                              setSpfGraph(graph);
                              setSpfGraphError(null);
                            } catch (err) {
                              setSpfGraph(null);
                              setSpfGraphError((err as Error).message);
                            }
                          }}>Graph</Button>
                        </div>
                        {spfSimResult && (
                          <div className="text-sm mt-2">Result: <strong>{spfSimResult.result}</strong> (lookups: {spfSimResult.lookups})<div className="text-xs text-muted">{spfSimResult.reasons.join(', ')}</div></div>
                        )}
                        {spfGraphError && (
                          <div className="text-sm text-red-600 mt-2">Error building graph: {spfGraphError}</div>
                        )}
                        {spfGraph && (
                          <div className="text-sm mt-2"><div>Graph:</div><pre className="whitespace-pre-wrap">{JSON.stringify(spfGraph, null, 2)}</pre></div>
                        )}
                      </div>
                    </div>
                  );
                default:
                  return (
                    <Input
                      value={record.content}
                      onChange={(e: ChangeEvent<HTMLInputElement>) => onRecordChange({
                        ...record,
                        content: e.target.value
                      })}
                      placeholder="e.g., 192.168.1.1"
                    />
                  );
              }
            })()}
          </div>
          {record.type === 'MX' && (
            <div className="space-y-2">
              <Label>Priority</Label>
              <Input
                type="number"
                value={record.priority || ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => {
                  const n = Number.parseInt(e.target.value, 10);
                  onRecordChange({
                    ...record,
                    priority: Number.isNaN(n) ? undefined : n
                  });
                }}
              />
            </div>
          )}
          {(record.type === 'A' || record.type === 'AAAA' || record.type === 'CNAME') && (
            <div className="flex items-center space-x-2">
              <Switch
                checked={record.proxied || false}
                onCheckedChange={(checked: boolean) =>
                  onRecordChange({
                    ...record,
                    proxied: checked
                  })
                }
              />
              <Label>Proxied through Cloudflare</Label>
            </div>
          )}
          <Button onClick={onAdd} className="w-full">
            Create Record
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

/**
 * Simple import dialog to paste JSON payloads and import DNS records into the
 * current zone. This component is a light overlay that validates JSON
 * client-side and forwards the payload to the parent for processing.
 */
import { ImportExportDialog } from './import-export-dialog';

/**
 * Deprecated wrapper that forwards to ImportExportDialog with JSON format.
 * Kept for compatibility with older imports.
 */
export function ImportRecordsDialog({ open, onOpenChange, data, onDataChange, onImport }: any) {
  return (
    <ImportExportDialog
      open={open}
      onOpenChange={onOpenChange}
      importData={data}
      importFormat={'json'}
      onImportDataChange={onDataChange}
      onImportFormatChange={() => {}}
      onImport={onImport}
      onExport={() => {}}
    />
  );
}

/**
 * Props for a dialog to import JSON-formatted DNS records into the
 * application. This is a simplified dialog used in the UI to paste JSON
 * text.
 */
export interface ImportRecordsDialogProps {
  /** Whether the dialog is open */
  open: boolean;
  /** Callback invoked when the dialog open state changes */
  onOpenChange: (open: boolean) => void;
  /** The JSON data being imported */
  data: string;
  /** Callback when the import data field changes */
  onDataChange: (val: string) => void;
  /** Callback to trigger the import */
  onImport: () => void;
}

/**
 * Component rendering an Import dialog that accepts JSON payload for DNS
 * records and invokes `onImport` when the user accepts.
 */
// deprecated: handled by ImportExportDialog wrapper above

/**
 * UI component rendering a single DNS record row and optional inline
 * editor allowing update and deletion of the record.
 */
import { useState, useEffect } from 'react';
import type { ChangeEvent } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import type { RecordType, DNSRecord, TTLValue } from '@/types/dns';
import { parseSPF, composeSPF, validateSPF } from '@/lib/spf';
import { RECORD_TYPES, getTTLPresets, getRecordTypeLabel } from '@/types/dns';
import { Edit2, Trash2, Save, X } from 'lucide-react';


/**
 * Properties for the `RecordRow` UI component which renders and optionally
 * edits a DNS record.
 */
export interface RecordRowProps {
  /** The DNS record to display or edit */
  record: DNSRecord;
  /** Whether the row is currently in edit mode */
  isEditing: boolean;
  /** Callback invoked to transition into edit mode */
  onEdit: () => void;
  /** Save callback after editing; receives the updated record */
  onSave: (record: DNSRecord) => void;
  /** Cancel editing and revert changes */
  onCancel: () => void;
  /** Remove the record */
  onDelete: () => void;
}

/**
 * Render a single DNS record row. When `isEditing` is true the row
 * renders in edit mode with inputs for each editable field; otherwise it
 * displays the record details.
 */
export function RecordRow({ record, isEditing, onEdit, onSave, onCancel, onDelete }: RecordRowProps) {
  const [editedRecord, setEditedRecord] = useState(record);
  const parseSRV = (content?: string) => {
    if (!content) return { priority: undefined, weight: undefined, port: undefined, target: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 4) return { priority: undefined, weight: undefined, port: undefined, target: content };
    const [priority, weight, port, ...rest] = parts;
    return { priority: Number(priority), weight: Number(weight), port: Number(port), target: rest.join(' ') };
  };
  const composeSRV = (p?: number, w?: number, prt?: number, t?: string) => `${p ?? 0} ${w ?? 0} ${prt ?? 0} ${t ?? ''}`;
  const [srvPriority, setSrvPriority] = useState<number | undefined>(parseSRV(record.content).priority);
  const [srvWeight, setSrvWeight] = useState<number | undefined>(parseSRV(record.content).weight);
  const [srvPort, setSrvPort] = useState<number | undefined>(parseSRV(record.content).port);
  const [srvTarget, setSrvTarget] = useState<string>(parseSRV(record.content).target ?? '');
  const parseTLSA = (content?: string) => {
    if (!content) return { usage: undefined, selector: undefined, matchingType: undefined, data: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 4) return { usage: undefined, selector: undefined, matchingType: undefined, data: content };
    const [usage, selector, matchingType, ...rest] = parts;
    return { usage: Number(usage), selector: Number(selector), matchingType: Number(matchingType), data: rest.join(' ') };
  };
  const composeTLSA = (u?: number, s?: number, m?: number, d?: string) => `${u ?? 0} ${s ?? 0} ${m ?? 0} ${d ?? ''}`;
  const [tlsaUsage, setTlsaUsage] = useState<number | undefined>(parseTLSA(record.content).usage);
  const [tlsaSelector, setTlsaSelector] = useState<number | undefined>(parseTLSA(record.content).selector);
  const [tlsaMatchingType, setTlsaMatchingType] = useState<number | undefined>(parseTLSA(record.content).matchingType);
  const [tlsaData, setTlsaData] = useState<string>(parseTLSA(record.content).data ?? '');
  const parseSSHFP = (content?: string) => {
    if (!content) return { algorithm: undefined, fptype: undefined, fingerprint: '' };
    const parts = String(content).trim().split(/\s+/);
    if (parts.length < 3) return { algorithm: undefined, fptype: undefined, fingerprint: content };
    const [algorithm, fptype, ...rest] = parts;
    return { algorithm: Number(algorithm), fptype: Number(fptype), fingerprint: rest.join(' ') };
  };
  const composeSSHFP = (a?: number, f?: number, fp?: string) => `${a ?? 0} ${f ?? 0} ${fp ?? ''}`;
  const [sshfpAlgorithm, setSshfpAlgorithm] = useState<number | undefined>(parseSSHFP(record.content).algorithm);
  const [sshfpFptype, setSshfpFptype] = useState<number | undefined>(parseSSHFP(record.content).fptype);
  const [sshfpFingerprint, setSshfpFingerprint] = useState<string>(parseSSHFP(record.content).fingerprint ?? '');
  const splitNaptrTokens = (s: string) => {
    const tokens: string[] = [];
    let current = '';
    let inQuote = false;
    for (let i = 0; i < s.length; i++) {
      const ch = s[i];
      if (ch === '"') {
        inQuote = !inQuote;
        current += ch;
        continue;
      }
      if (ch === ' ' && !inQuote) {
        if (current.trim().length > 0) {
          tokens.push(current.trim());
          current = '';
        }
        continue;
      }
      current += ch;
    }
    if (current.trim().length > 0) tokens.push(current.trim());
    return tokens;
  };

  const parseNAPTR = (content?: string) => {
    if (!content) return { order: undefined, preference: undefined, flags: '', service: '', regexp: '', replacement: '' };
    const tokens = splitNaptrTokens(String(content).trim());
    const [order, preference, flags, service, regexp, replacement] = tokens;
    return { order: Number(order), preference: Number(preference), flags: flags?.replace(/^"|"$/g, ''), service, regexp: regexp?.replace(/^"|"$/g, ''), replacement };
  };
  const quoteIfNeeded = (s?: string) => {
    if (!s) return '';
    if (/\s/.test(s) || /"/.test(s)) return `"${s.replace(/"/g, '\\"')}"`;
    return s;
  };
  const composeNAPTR = (o?: number, p?: number, f?: string, s?: string, r?: string, rep?: string) => `${o ?? 0} ${p ?? 0} ${f ?? ''} ${s ?? ''} ${quoteIfNeeded(r)} ${rep ?? ''}`;
  const [naptrOrder, setNaptrOrder] = useState<number | undefined>(parseNAPTR(record.content).order);
  const [naptrPref, setNaptrPref] = useState<number | undefined>(parseNAPTR(record.content).preference);
  const [naptrFlags, setNaptrFlags] = useState<string>(parseNAPTR(record.content).flags ?? '');
  const [naptrService, setNaptrService] = useState<string>(parseNAPTR(record.content).service ?? '');
  const [naptrRegexp, setNaptrRegexp] = useState<string>(parseNAPTR(record.content).regexp ?? '');
  const [naptrReplacement, setNaptrReplacement] = useState<string>(parseNAPTR(record.content).replacement ?? '');

  useEffect(() => {
    setEditedRecord(record);
    if (record.type === 'SRV') {
      const parsed = parseSRV(record.content);
      setSrvPriority(parsed.priority);
      setSrvWeight(parsed.weight);
      setSrvPort(parsed.port);
      setSrvTarget(parsed.target ?? '');
    }
    if (record.type === 'TLSA') {
      const parsed = parseTLSA(record.content);
      setTlsaUsage(parsed.usage);
      setTlsaSelector(parsed.selector);
      setTlsaMatchingType(parsed.matchingType);
      setTlsaData(parsed.data ?? '');
    }
    if (record.type === 'SSHFP') {
      const parsed = parseSSHFP(record.content);
      setSshfpAlgorithm(parsed.algorithm);
      setSshfpFptype(parsed.fptype);
      setSshfpFingerprint(parsed.fingerprint ?? '');
    }
    if (record.type === 'NAPTR') {
      const parsed = parseNAPTR(record.content);
      setNaptrOrder(parsed.order);
      setNaptrPref(parsed.preference);
      setNaptrFlags(parsed.flags ?? '');
      setNaptrService(parsed.service ?? '');
      setNaptrRegexp(parsed.regexp ?? '');
      setNaptrReplacement(parsed.replacement ?? '');
    }
  }, [record]);

  const ttlValue = editedRecord.ttl === 1 ? 'auto' : editedRecord.ttl;
  const isCustomTTL = !getTTLPresets().includes(ttlValue as TTLValue);

  if (isEditing) {
    return (
      <div className="p-4 border rounded-lg bg-muted/50">
        <div className="grid grid-cols-12 gap-4 items-center">
          <div className="col-span-2">
            <Select
              value={editedRecord.type}
              onValueChange={(value: RecordType) => setEditedRecord({
                ...editedRecord,
                type: value
              })}
            >
              <SelectTrigger className="h-8">
                <SelectValue />
              </SelectTrigger>
                <SelectContent>
                  {RECORD_TYPES.map((type) => (
                    <SelectItem key={type} value={type} title={getRecordTypeLabel(type)}>
                      {getRecordTypeLabel(type)}
                    </SelectItem>
                  ))}
                </SelectContent>
            </Select>
          </div>
          <div className="col-span-3">
            <Input
              value={editedRecord.name}
              onChange={(e: ChangeEvent<HTMLInputElement>) => setEditedRecord({
                ...editedRecord,
                name: e.target.value
              })}
              className="h-8"
            />
          </div>
          <div className="col-span-4">
            {editedRecord.type === 'SRV' ? (
              <div className="grid grid-cols-4 gap-2">
                <Input
                  type="number"
                  placeholder="priority"
                  value={srvPriority ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setSrvPriority(val);
                    setEditedRecord({ ...editedRecord, content: composeSRV(val, srvWeight, srvPort, srvTarget) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="weight"
                  value={srvWeight ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setSrvWeight(val);
                    setEditedRecord({ ...editedRecord, content: composeSRV(srvPriority, val, srvPort, srvTarget) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="port"
                  value={srvPort ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setSrvPort(val);
                    setEditedRecord({ ...editedRecord, content: composeSRV(srvPriority, srvWeight, val, srvTarget) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="target"
                  value={srvTarget}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setSrvTarget(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeSRV(srvPriority, srvWeight, srvPort, e.target.value) });
                  }}
                  className="h-8"
                />
              </div>
            ) : editedRecord.type === 'TLSA' ? (
              <div className="grid grid-cols-4 gap-2">
                <Input
                  type="number"
                  placeholder="usage"
                  value={tlsaUsage ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setTlsaUsage(val);
                    setEditedRecord({ ...editedRecord, content: composeTLSA(val, tlsaSelector, tlsaMatchingType, tlsaData) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="selector"
                  value={tlsaSelector ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setTlsaSelector(val);
                    setEditedRecord({ ...editedRecord, content: composeTLSA(tlsaUsage, val, tlsaMatchingType, tlsaData) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="matching type"
                  value={tlsaMatchingType ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setTlsaMatchingType(val);
                    setEditedRecord({ ...editedRecord, content: composeTLSA(tlsaUsage, tlsaSelector, val, tlsaData) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="data"
                  value={tlsaData}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setTlsaData(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeTLSA(tlsaUsage, tlsaSelector, tlsaMatchingType, e.target.value) });
                  }}
                  className="h-8"
                />
              </div>
            ) : editedRecord.type === 'SSHFP' ? (
              <div className="grid grid-cols-3 gap-2">
                <Input
                  type="number"
                  placeholder="algorithm"
                  value={sshfpAlgorithm ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setSshfpAlgorithm(val);
                    setEditedRecord({ ...editedRecord, content: composeSSHFP(val, sshfpFptype, sshfpFingerprint) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="fptype"
                  value={sshfpFptype ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setSshfpFptype(val);
                    setEditedRecord({ ...editedRecord, content: composeSSHFP(sshfpAlgorithm, val, sshfpFingerprint) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="fingerprint"
                  value={sshfpFingerprint}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setSshfpFingerprint(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeSSHFP(sshfpAlgorithm, sshfpFptype, e.target.value) });
                  }}
                  className="h-8"
                />
              </div>
            ) : editedRecord.type === 'NAPTR' ? (
              <div className="grid grid-cols-6 gap-2">
                <Input
                  type="number"
                  placeholder="order"
                  value={naptrOrder ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setNaptrOrder(val);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(val, naptrPref, naptrFlags, naptrService, naptrRegexp, naptrReplacement) });
                  }}
                  className="h-8"
                />
                <Input
                  type="number"
                  placeholder="preference"
                  value={naptrPref ?? ''}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    const n = Number.parseInt(e.target.value, 10);
                    const val = Number.isNaN(n) ? undefined : n;
                    setNaptrPref(val);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(naptrOrder, val, naptrFlags, naptrService, naptrRegexp, naptrReplacement) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="flags"
                  value={naptrFlags}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setNaptrFlags(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(naptrOrder, naptrPref, e.target.value, naptrService, naptrRegexp, naptrReplacement) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="service"
                  value={naptrService}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setNaptrService(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, e.target.value, naptrRegexp, naptrReplacement) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="regexp"
                  value={naptrRegexp}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setNaptrRegexp(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, naptrService, e.target.value, naptrReplacement) });
                  }}
                  className="h-8"
                />
                <Input
                  placeholder="replacement"
                  value={naptrReplacement}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => {
                    setNaptrReplacement(e.target.value);
                    setEditedRecord({ ...editedRecord, content: composeNAPTR(naptrOrder, naptrPref, naptrFlags, naptrService, naptrRegexp, e.target.value) });
                  }}
                  className="h-8"
                />
              </div>
            ) : editedRecord.type === 'SPF' ? (
              <div className="space-y-2">
                <Input
                  value={editedRecord.content}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => setEditedRecord({ ...editedRecord, content: e.target.value })}
                  className="h-8"
                />
                <div className="flex space-x-2">
                  <select className="h-8 p-2" value={''} onChange={() => { /* small builder not implemented yet */ }}>
                    <option value="">+</option>
                  </select>
                  <span className="text-sm">Preview: {composeSPF(parseSPF(editedRecord.content) ?? { version: 'v=spf1', mechanisms: [] })}</span>
                </div>
                {!validateSPF(editedRecord.content).ok && (
                  <div className="text-red-600">SPF validation issues: {validateSPF(editedRecord.content).problems.join(', ')}</div>
                )}
              </div>
            ) : (
              <Input
                value={editedRecord.content}
                onChange={(e: ChangeEvent<HTMLInputElement>) => setEditedRecord({
                  ...editedRecord,
                  content: e.target.value
                })}
                className="h-8"
              />
            )}
          </div>
          <div className="col-span-1 space-y-1">
            <Select
              value={isCustomTTL ? 'custom' : String(ttlValue)}
              onValueChange={(value: string) => {
                if (value === 'custom') {
                  setEditedRecord({ ...editedRecord, ttl: 300 });
                } else {
                  setEditedRecord({
                    ...editedRecord,
                    ttl: value === 'auto' ? 'auto' : Number(value),
                  });
                }
              }}
            >
              <SelectTrigger className="h-8">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {getTTLPresets().map((ttl) => (
                  <SelectItem key={ttl} value={String(ttl)}>
                    {ttl === 'auto' ? 'Auto' : ttl}
                  </SelectItem>
                ))}
                <SelectItem value="custom">Custom</SelectItem>
              </SelectContent>
            </Select>
            {isCustomTTL && (
              <Input
                type="number"
                value={typeof editedRecord.ttl === 'number' ? editedRecord.ttl : ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => {
                  const n = Number.parseInt(e.target.value, 10);
                  setEditedRecord({
                    ...editedRecord,
                    ttl: Number.isNaN(n) ? 300 : n,
                  });
                }}
                className="h-8"
              />
            )}
            {editedRecord.type === 'MX' && (
              <Input
                type="number"
                value={editedRecord.priority ?? ''}
                onChange={(e: ChangeEvent<HTMLInputElement>) => {
                  const n = Number.parseInt(e.target.value, 10);
                  setEditedRecord({
                    ...editedRecord,
                    priority: Number.isNaN(n) ? undefined : n,
                  });
                }}
                className="h-8"
              />
            )}
          </div>
          <div className="col-span-1">
            {(editedRecord.type === 'A' || editedRecord.type === 'AAAA' || editedRecord.type === 'CNAME') && (
              <Switch
                checked={editedRecord.proxied || false}
                onCheckedChange={(checked: boolean) => setEditedRecord({
                  ...editedRecord,
                  proxied: checked
                })}
              />
            )}
          </div>
          <div className="col-span-1 flex gap-1">
            <Button
              size="sm"
              onClick={() => onSave(editedRecord)}
              className="h-8 w-8 p-0"
            >
              <Save className="h-3 w-3" />
            </Button>
            <Button
              size="sm"
              variant="outline"
              onClick={onCancel}
              className="h-8 w-8 p-0"
            >
              <X className="h-3 w-3" />
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 border rounded-lg hover:bg-muted/50 transition-colors">
      <div className="grid grid-cols-12 gap-4 items-center">
        <div className="col-span-2">
          <span title={getRecordTypeLabel(record.type as RecordType)} className="inline-flex items-center px-2 py-1 rounded-md text-xs font-medium bg-primary/10 text-primary">
            {record.type}
          </span>
        </div>
        <div className="col-span-3">
          <span className="font-mono text-sm">{record.name}</span>
        </div>
        <div className="col-span-4">
          <span className="font-mono text-sm break-all">{record.content}</span>
        </div>
        <div className="col-span-1">
          <span className="text-sm text-muted-foreground">
            {record.ttl === 1 ? 'Auto' : record.ttl}
          </span>
        </div>
        <div className="col-span-1">
          {record.proxied && (
            <span className="inline-flex items-center px-2 py-1 rounded-md text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
              Proxied
            </span>
          )}
        </div>
        <div className="col-span-1 flex gap-1">
          <Button
            size="sm"
            variant="ghost"
            onClick={onEdit}
            className="h-8 w-8 p-0"
          >
            <Edit2 className="h-3 w-3" />
          </Button>
          <Button
            size="sm"
            variant="ghost"
            onClick={onDelete}
            className="h-8 w-8 p-0 text-destructive hover:text-destructive"
          >
            <Trash2 className="h-3 w-3" />
          </Button>
        </div>
      </div>
    </div>
  );
}

/**
 * Top-level DNS Manager UI which composes the zone selector, the record
 * list and dialogs for creating/importing records.
 */
import { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useCloudflareAPI } from '@/hooks/use-cloudflare-api';
import type { DNSRecord, Zone, RecordType } from '@/types/dns';
import { RECORD_TYPES } from '@/types/dns';
import { useToast } from '@/hooks/use-toast';
import { useTranslation } from 'react-i18next';
import { storageManager } from '@/lib/storage';
import { LogOut } from 'lucide-react';
import { AddRecordDialog } from './AddRecordDialog';
import { ImportExportDialog } from './import-export-dialog';
import { RecordRow } from './RecordRow';
import { FixedSizeList as List } from 'react-window';
import { filterRecords } from './filter-records';
import { parseCSVRecords, parseBINDZone } from '@/lib/dns-parsers';

/**
 * Props for the `DNSManager` top-level component.
 */
interface DNSManagerProps {
  /** API key to be used for server requests (token or key) */
  apiKey: string;
  /** Optional email used when authenticating with a key+email pair */
  email?: string;
  /** Callback invoked when the user logs out */
  onLogout: () => void;
}


/**
 * DNS Manager component responsible for listing zones and DNS records and
 * providing UI for add/import/export/update/delete operations.
 *
 * @param apiKey - API key used to authenticate server requests
 * @param email - optional email when using key+email authentication
 * @param onLogout - callback invoked when the user logs out
 */
export function DNSManager({ apiKey, email, onLogout }: DNSManagerProps) {
  const { t } = useTranslation();
  const [zones, setZones] = useState<Zone[]>([]);
  const [selectedZone, setSelectedZone] = useState<string>('');
  const [records, setRecords] = useState<DNSRecord[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [editingRecord, setEditingRecord] = useState<string | null>(null);
  const [showAddRecord, setShowAddRecord] = useState(false);
  const [showImport, setShowImport] = useState(false);
  const [newRecord, setNewRecord] = useState<Partial<DNSRecord>>({
    type: 'A',
    name: '',
    content: '',
    ttl: 300,
    proxied: false
  });
  const [importData, setImportData] = useState('');
  const [importFormat, setImportFormat] = useState<'json' | 'csv' | 'bind'>('json');
  const [searchTerm, setSearchTerm] = useState('');
  const [typeFilter, setTypeFilter] = useState<RecordType | ''>('');
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(50);
  const [autoRefreshInterval, setAutoRefreshInterval] = useState<number | null>(storageManager.getAutoRefreshInterval());
  
  const { toast } = useToast();
  const {
    getZones,
    getDNSRecords,
    createDNSRecord,
    updateDNSRecord,
    bulkCreateDNSRecords,
    deleteDNSRecord,
    exportDNSRecords,
  } = useCloudflareAPI(apiKey, email);

  const loadZones = useCallback(async (signal?: AbortSignal) => {
    try {
      setIsLoading(true);
      const zonesData = await getZones(signal);
      setZones(zonesData);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load zones: " + (error as Error).message,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  }, [getZones, toast]);

  const loadRecords = useCallback(async (signal?: AbortSignal) => {
    if (!selectedZone) return;

    try {
      setIsLoading(true);
      const recordsData = await getDNSRecords(selectedZone, page, perPage, signal);
      setRecords(recordsData);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load DNS records: " + (error as Error).message,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  }, [getDNSRecords, selectedZone, page, perPage, toast]);

  useEffect(() => {
    const controller = new AbortController();
    loadZones(controller.signal);
    return () => controller.abort();
  }, [loadZones]);

  useEffect(() => {
    const last = storageManager.getLastZone();
    if (last) {
      setSelectedZone(last);
    }
  }, []);

  useEffect(() => {
    if (selectedZone) {
      const controller = new AbortController();
      loadRecords(controller.signal);
      return () => controller.abort();
    }
  }, [selectedZone, loadRecords]);

  useEffect(() => {
    if (selectedZone) {
      storageManager.setLastZone(selectedZone);
    }
  }, [selectedZone]);

  // Auto refresh: refresh records at the selected interval unless the user is
  // currently editing or has modal dialogs open to avoid disrupting work.
  useEffect(() => {
    storageManager.setAutoRefreshInterval(autoRefreshInterval ?? null);
    if (!autoRefreshInterval || autoRefreshInterval <= 0) return;
    const id = setInterval(async () => {
      if (editingRecord || showAddRecord || showImport) return;
      const controller = new AbortController();
      await loadRecords(controller.signal);
    }, autoRefreshInterval);
    return () => clearInterval(id);
  }, [autoRefreshInterval, editingRecord, showAddRecord, showImport, loadRecords]);

  const handleAddRecord = async () => {
    if (!selectedZone || !newRecord.type || !newRecord.name || !newRecord.content) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return;
    }

    try {
      const createdRecord = await createDNSRecord(selectedZone, newRecord);
      setRecords([createdRecord, ...records]);
      setNewRecord({
        type: 'A',
        name: '',
        content: '',
        ttl: 300,
        proxied: false
      });
      setShowAddRecord(false);
      
      toast({
        title: "Success",
        description: "DNS record created successfully"
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create DNS record: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleUpdateRecord = async (record: DNSRecord) => {
    try {
      const updatedRecord = await updateDNSRecord(selectedZone, record.id, record);
      setRecords(records.map((r: DNSRecord) => r.id === record.id ? updatedRecord : r));
      setEditingRecord(null);
      
      toast({
        title: "Success",
        description: "DNS record updated successfully"
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update DNS record: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleDeleteRecord = async (recordId: string) => {
    try {
      await deleteDNSRecord(selectedZone, recordId);
      setRecords(records.filter((r: DNSRecord) => r.id !== recordId));
      
      toast({
        title: "Success",
        description: "DNS record deleted successfully"
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete DNS record: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleExport = (format: 'json' | 'csv' | 'bind') => {
    let content = '';
    let filename = '';
    let mimeType = '';

    switch (format) {
      case 'json': {
        content = JSON.stringify(records, null, 2);
        filename = `${selectedZone}-records.json`;
        mimeType = 'application/json';
        break;
      }
      case 'csv': {
        const headers = ['Type', 'Name', 'Content', 'TTL', 'Priority', 'Proxied'];
        const escapeCSV = (value: unknown) =>
          `"${String(value ?? '').replace(/"/g, '""')}"`;
        const rows = records
          .map((r: DNSRecord) =>
            [
              r.type,
              r.name,
              r.content,
              r.ttl,
              r.priority ?? '',
              r.proxied ?? false,
            ]
              .map(escapeCSV)
              .join(',')
          )
          .join('\n');
        content = headers.map(escapeCSV).join(',') + '\n' + rows;
        filename = `${selectedZone}-records.csv`;
        mimeType = 'text/csv';
        break;
      }
      case 'bind': {
        content = records
          .map((r: DNSRecord) => {
            const ttl = r.ttl || 300;
            const priority = r.priority ? `${r.priority} ` : '';
            return `${r.name}\t${ttl}\tIN\t${r.type}\t${priority}${r.content}`;
          })
          .join('\n');
        filename = `${selectedZone}.zone`;
        mimeType = 'text/plain';
        break;
      }
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Success",
      description: `Records exported as ${format.toUpperCase()}`
    });
  };

  const handleImport = async (providedItems?: Partial<DNSRecord>[], dryRun?: boolean) => {
    try {
      let items: Partial<DNSRecord>[] | null = null;
      if (providedItems) items = providedItems as Partial<DNSRecord>[];
      else {
      switch (importFormat) {
        case 'json': {
          const imported = JSON.parse(importData);
          items = Array.isArray(imported)
            ? imported
            : Array.isArray(imported.records)
              ? imported.records
              : null;
          break;
        }
        case 'csv':
          items = parseCSVRecords(importData);
          break;
        case 'bind':
          items = parseBINDZone(importData);
          break;
      }
      }

      if (!items) {
        throw new Error('Invalid format');
      }

      const valid: DNSRecord[] = [];
      let skipped = 0;

      for (const item of items) {
        if (item && item.type && item.name && item.content) {
          const exists = records.some(
            r => r.type === item.type && r.name === item.name && r.content === item.content
          );
          if (!exists) {
            valid.push(item as DNSRecord);
          } else {
            skipped++;
          }
        } else {
          skipped++;
        }
      }

      if (valid.length) {
        if (bulkCreateDNSRecords) {
          try {
            const result = await bulkCreateDNSRecords(selectedZone, valid, dryRun);
            // Handle created and skipped results returned by server
            const created = Array.isArray((result as any).created) ? (result as any).created : valid;
            setRecords([...created, ...records]);
          } catch (err) {
            toast({
              title: 'Error',
              description: 'Failed to import records: ' + (err as Error).message,
              variant: 'destructive',
            });
            return;
          }
        } else {
          // Fall back to local creation using per-record API or local state
          const createdRecords: DNSRecord[] = [];
          for (const v of valid) {
            try {
              const r = await createDNSRecord(selectedZone, v);
              createdRecords.push(r);
            } catch (err) {
              skipped++;
            }
          }
          setRecords([...createdRecords, ...records]);
        }
        setImportData('');
        setShowImport(false);

        toast({
          title: "Success",
          description: `Imported ${valid.length} record(s)` + (skipped ? `, skipped ${skipped}` : '')
        });
      } else {
        toast({
          title: "Error",
          description: skipped
            ? `No new records imported. Skipped ${skipped} invalid or duplicate item(s).`
            : 'No valid records found.',
          variant: 'destructive'
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to import records: " + (error as Error).message,
        variant: "destructive"
      });
    }
  };

  const handleLogout = () => {
    storageManager.clearSession();
    onLogout();
  };

  const selectedZoneData = zones.find((z: Zone) => z.id === selectedZone);
  const filteredRecords = filterRecords(records, searchTerm).filter(
    (record: DNSRecord) => (typeFilter ? record.type === typeFilter : true)
  );

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl">{t('DNS Manager', 'DNS Manager')}</CardTitle>
                <p className="text-muted-foreground">{t('Manage your Cloudflare DNS records', 'Manage your Cloudflare DNS records')}</p>
              </div>
              <Button onClick={handleLogout} variant="outline">
                <LogOut className="h-4 w-4 mr-2" />
                Logout
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4 items-end">
              <div className="flex-1">
                <Label htmlFor="zone-select">Domain/Zone</Label>
                <Select value={selectedZone} onValueChange={setSelectedZone}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select a domain" />
                  </SelectTrigger>
                  <SelectContent>
                    {zones.map((zone: Zone) => (
                      <SelectItem key={zone.id} value={zone.id}>
                        {zone.name} ({zone.status})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              {selectedZone && (
                <div className="flex gap-2">
                  <AddRecordDialog
                    open={showAddRecord}
                    onOpenChange={setShowAddRecord}
                    record={newRecord}
                    onRecordChange={setNewRecord}
                    onAdd={handleAddRecord}
                    zoneName={selectedZoneData?.name}
                    apiKey={apiKey}
                    email={email}
                  />
                </div>
                )}
            </div>
          </CardContent>
        </Card>

        {/* DNS Records */}
        {selectedZone && (
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>DNS Records</CardTitle>
                <div className="flex gap-2 items-center">
                  <Input
                    placeholder={t('Search records', 'Search records')}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-[200px]"
                  />
                  <ImportExportDialog
                    open={showImport}
                    onOpenChange={setShowImport}
                    importData={importData}
                    importFormat={importFormat}
                    onImportDataChange={setImportData}
                    onImportFormatChange={setImportFormat}
                    onImport={handleImport}
                    serverExport={async (format) => {
                      if (!selectedZone) return;
                      try {
                        const res = await exportDNSRecords(selectedZone, format, page, perPage);
                        // create a blob and download
                        const blob = new Blob([res], { type: format === 'json' ? 'application/json' : format === 'csv' ? 'text/csv' : 'text/plain' });
                        const url = URL.createObjectURL(blob);
                        const a = document.createElement('a');
                        a.href = url;
                        a.download = `${selectedZone}-records.${format === 'json' ? 'json' : format === 'csv' ? 'csv' : 'zone'}`;
                        a.click();
                        URL.revokeObjectURL(url);
                        toast({ title: 'Success', description: `Server export ${format.toUpperCase()} completed` });
                      } catch (err) {
                        toast({ title: 'Error', description: 'Server export failed: ' + (err as Error).message, variant: 'destructive' });
                      }
                    }}
                    onExport={handleExport}
                  />
                  <Select value={String(autoRefreshInterval ?? 0)} onValueChange={(v) => setAutoRefreshInterval(v ? Number(v) : null)}>
                    <SelectTrigger className="w-32">
                      <SelectValue placeholder="Auto-refresh" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="0">Off</SelectItem>
                      <SelectItem value="60000">1 min</SelectItem>
                      <SelectItem value="300000">5 min</SelectItem>
                      <SelectItem value="600000">10 min</SelectItem>
                      <SelectItem value="1800000">30 min</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="flex justify-end mb-4">
                    <Select value={typeFilter} onValueChange={(v) => setTypeFilter(v as RecordType | '')}>
                  <SelectTrigger className="w-[180px]">
                    <SelectValue placeholder="All types" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">{t('All types', 'All types')}</SelectItem>
                    {RECORD_TYPES.map((type) => (
                      <SelectItem key={type} value={type}>
                        {type}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <div className="ml-4 flex items-center gap-2">
                  <Button size="sm" variant="outline" onClick={() => setPage((p) => Math.max(1, p - 1))} disabled={page <= 1}>Prev</Button>
                  <div className="text-sm">Page {page}</div>
                  <Button size="sm" variant="outline" onClick={() => setPage((p) => p + 1)}>Next</Button>
                  <Select value={String(perPage)} onValueChange={(v) => setPerPage(Number(v) )}>
                    <SelectTrigger className="w-28 ml-2">
                      <SelectValue placeholder="Per page" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="25">25</SelectItem>
                      <SelectItem value="50">50</SelectItem>
                      <SelectItem value="100">100</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
                {isLoading ? (
                <div className="text-center py-8">Loading...</div>
              ) : filteredRecords.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  {t('No DNS records found', 'No DNS records found')}
                </div>
              ) : (
                <div className="space-y-2">
                  <List
                    height={Math.min(600, filteredRecords.length * 72)}
                    itemCount={filteredRecords.length}
                    itemSize={72}
                    width={'100%'}
                    >{({ index, style }: { index: number; style: React.CSSProperties }) => {
                    const record = filteredRecords[index];
                    return (
                      <div style={style} key={record.id}>
                        <RecordRow
                          record={record}
                          isEditing={editingRecord === record.id}
                          onEdit={() => setEditingRecord(record.id)}
                          onSave={(updatedRecord: DNSRecord) => handleUpdateRecord(updatedRecord)}
                          onCancel={() => setEditingRecord(null)}
                          onDelete={() => handleDeleteRecord(record.id)}
                        />
                      </div>
                    );
                  }}</List>
                </div>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

/**
 * UI controls to import and export DNS records for the selected zone.
 * This component exposes actions and a modal allowing the user to import
 * JSON/CSV/BIND data.
 */
import type { ChangeEvent } from 'react';
import { useState } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Upload, Download } from 'lucide-react';
import { parseCSVRecords, parseBINDZone } from '@/lib/dns-parsers';
import { ImportPreviewDialog } from './ImportPreviewDialog';
import type { DNSRecord } from '@/types/dns';

/**
 * Props for the import/export dialog used to import DNS records into
 * the current zone or export the records in a selected format.
 */
interface ImportExportDialogProps {
  /** Whether the dialog is open */
  open: boolean;
  /** Callback invoked when the dialog open state changes */
  onOpenChange: (open: boolean) => void;
  /** The raw data being imported */
  importData: string;
  /** Expected import format */
  importFormat: 'json' | 'csv' | 'bind';
  /** Callback invoked when user edits the import data */
  onImportDataChange: (data: string) => void;
  /** Callback invoked when user selects a different import format */
  onImportFormatChange: (format: 'json' | 'csv' | 'bind') => void;
  /** Callback invoked to perform the import */
  onImport: (items?: Partial<DNSRecord>[], dryRun?: boolean) => void;
  /** Callback to export current records using the selected format */
  onExport: (format: 'json' | 'csv' | 'bind') => void;
  serverExport?: (format: 'json' | 'csv' | 'bind') => Promise<void>;
}

/**
 * Render an Import/Export control used by the DNS Manager for importing
 * JSON/CSV/BIND formats or exporting the current records.
 */
export function ImportExportDialog({
  open,
  onOpenChange,
  importData,
  importFormat,
  onImportDataChange,
  onImportFormatChange,
  onImport,
  onExport,
  serverExport
}: ImportExportDialogProps) {
  const [previewItems, setPreviewItems] = useState<Partial<DNSRecord>[]>([]);
  const [showPreview, setShowPreview] = useState(false);
  return (
    <div className="flex gap-2">
      <Dialog open={open} onOpenChange={onOpenChange}>
        <DialogTrigger asChild>
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-2" />
            Import
          </Button>
        </DialogTrigger>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Import DNS Records</DialogTitle>
            <DialogDescription>
              Import DNS records from JSON, CSV, or BIND zone formats
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div className="space-y-2">
              <Label>Format</Label>
              <Select value={importFormat} onValueChange={onImportFormatChange}>
                <SelectTrigger>
                  <SelectValue placeholder="Select format" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="json">JSON</SelectItem>
                  <SelectItem value="csv">CSV</SelectItem>
                  <SelectItem value="bind">BIND</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>{importFormat.toUpperCase()} Data</Label>
              <textarea
                className="w-full h-32 p-2 border rounded-md bg-background"
                value={importData}
                onChange={(e: ChangeEvent<HTMLTextAreaElement>) => onImportDataChange(e.target.value)}
                placeholder={`Paste your ${importFormat.toUpperCase()} data here...`}
              />
            </div>
            <Button onClick={() => {
              // Parse and show preview based on selected import format
              let items: Partial<Record<string, unknown>>[] | null = null;
              try {
                switch (importFormat) {
                  case 'json': {
                    const parsed = JSON.parse(importData);
                    items = Array.isArray(parsed)
                      ? parsed
                      : Array.isArray(parsed.records)
                        ? parsed.records
                        : null;
                    break;
                  }
                  case 'csv':
                    items = parseCSVRecords ? parseCSVRecords(importData) : null;
                    break;
                  case 'bind':
                    items = parseBINDZone ? parseBINDZone(importData) : null;
                    break;
                }
              } catch (e) {
                items = null;
              }

              if (!items) {
                onImport();
                return;
              }
              setPreviewItems(items as Partial<DNSRecord>[]);
              setShowPreview(true);
            }} className="w-full">
              Import Records
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      <Select onValueChange={(format: 'json' | 'csv' | 'bind') => onExport(format)}>
        <SelectTrigger className="w-32">
          <Download className="h-4 w-4 mr-2" />
          <SelectValue placeholder="Export" />
        </SelectTrigger>
        <SelectContent>
          <SelectItem value="json">JSON</SelectItem>
          <SelectItem value="csv">CSV</SelectItem>
          <SelectItem value="bind">BIND</SelectItem>
        </SelectContent>
      </Select>
      
      <Select onValueChange={async (format: 'json' | 'csv' | 'bind') => {
        if (serverExport) {
          try {
            await serverExport(format);
          } catch (err) {
            console.error('Server export failed', err);
          }
        }
      }}>
        <SelectTrigger className="w-28 ml-2">
          <SelectValue placeholder="Server Export" />
        </SelectTrigger>
        <SelectContent>
          <SelectItem value="json">JSON (server)</SelectItem>
          <SelectItem value="csv">CSV (server)</SelectItem>
          <SelectItem value="bind">BIND (server)</SelectItem>
        </SelectContent>
      </Select>
        {showPreview && (
          <ImportPreviewDialog
            open={showPreview}
            onOpenChange={setShowPreview}
            items={previewItems}
            onConfirm={(items: Partial<DNSRecord>[], dryRun?: boolean) => {
              // Forward parsed items and optionally run as dry-run to the caller
              onImport(items as Partial<DNSRecord>[], dryRun);
              setShowPreview(false);
            }}
            onCancel={() => setShowPreview(false)}
          />
        )}
      </div>
  );
}

/**
 * Simple audit logger used to record actions performed on sensitive objects.
 * For now this logs to console. Later it can be extended to write to a DB or
 * a rotating file.
 */
export type AuditEntry = {
  actor?: string;
  operation: string;
  resource?: string;
  details?: unknown;
  timestamp?: string;
};

const entries: AuditEntry[] = [];
import createCredentialStore from './credential-store';
const store = createCredentialStore();

export function logAudit(entry: AuditEntry) {
  const final: AuditEntry = { ...entry, timestamp: new Date().toISOString() };
  entries.push(final);
  // Simple console log for now; in production route to structured log
  // or write to a secure audit store.
    try {
      console.info('AUDIT', JSON.stringify(final));
      // If sqlite store exists, write audit there too (fire-and-forget)
      try {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        (store as any).writeAudit?.(final)?.catch?.(() => undefined);
      } catch (_e) {
        // ignore
      }
    } catch (e) {
    // ignore errors during logging.
  }
}

export async function getAuditEntries(): Promise<AuditEntry[]> {
  // If DB store available return DB entries, otherwise in-memory
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  if ((store as any).getAuditEntries) {
    try {
      // convert DB rows into AuditEntry[]
      const rows = await (store as any).getAuditEntries();
      return rows.map((r: any) => ({ actor: r.actor, operation: r.operation, resource: r.resource, details: JSON.parse(r.details || '{}'), timestamp: r.timestamp }));
    } catch (_e) {
      return [...entries];
    }
  }
  return [...entries];
}

export function clearAuditEntries(): void {
  entries.length = 0;
}

/**
 * Cloudflare API wrapper
 *
 * This module provides a minimal wrapper around the Cloudflare SDK that
 * exposes the operations required by the application: listing zones and DNS
 * records, and creating/updating/deleting DNS records.
 *
 * It consolidates debug logging and request/response inspection behind a
 * DEBUG flag and re-exports a typed `CloudflareAPI` class with convenience
 * helper methods.
 */
import 'cloudflare/shims/web';
import Cloudflare from 'cloudflare';
import type { DNSRecord, Zone } from '@/types/dns';
import { getEnv, getEnvBool } from './env';

const DEFAULT_CLOUDFLARE_API_BASE = 'https://api.cloudflare.com/client/v4';
const DEBUG = getEnvBool('DEBUG_CF_API', 'VITE_DEBUG_CF_API');

/**
 * Client to interact with the Cloudflare REST API.
 *
 * The class wraps the official `cloudflare` library instance and exposes a
 * small, typed surface used by the app. Each method returns typed records or
 * throws on error.
 */
export class CloudflareAPI {
  private client: Cloudflare;

  /**
   * Create a Cloudflare API client for the given credentials.
   *
   * @param apiKey - API token or global API key
   * @param baseUrl - Optional Cloudflare API base (defaults to CLOUDFLARE_API_BASE)
   * @param email - Optional email for API key authentication (used with apiKey)
   */
  constructor(
    apiKey: string,
    baseUrl: string = getEnv(
      'CLOUDFLARE_API_BASE',
      'VITE_CLOUDFLARE_API_BASE',
      DEFAULT_CLOUDFLARE_API_BASE,
    )!,
    email?: string,
  ) {
    this.client = new Cloudflare({
      apiToken: email ? null : apiKey,
      apiKey: email ? apiKey : null,
      apiEmail: email ?? null,
      baseURL: String(baseUrl),
      fetch: fetch,
    });
    if (DEBUG) {
      console.debug('Initialized CloudflareAPI', {
        baseUrl: this.client.baseURL,
        email: email ? 'provided' : 'none',
      });
    }
  }



  /**
   * Log the outgoing request when DEBUG is enabled.
   *
   * @param path - relative path of the request
   * @param options - optional request metadata such as method and body
   */
  private debugRequest(path: string, options?: { method?: string; body?: unknown }) {
    if (!DEBUG) return;
    console.debug('CF API request:', {
      url: `${this.client.baseURL}${path}`,
      method: options?.method ?? 'GET',
      body: options?.body,
    });
  }

  /**
   * Log the response payload when DEBUG is enabled.
   *
   * @param data - the response data to log
   */
  private debugResponse(data: unknown) {
    if (!DEBUG) return;
    console.debug('CF API response:', data);
  }

  /**
   * Build a Cloudflare-compatible parameter object for record creation and
   * update endpoints. Fields with `undefined` values are omitted.
   *
   * @param zoneId - Cloudflare zone identifier
   * @param record - partial DNSRecord with fields to send
   * @returns the params object suitable for the Cloudflare SDK
   */
  private buildRecordParams(zoneId: string, record: Partial<DNSRecord>) {
    const params: Record<string, unknown> = {
      zone_id: zoneId,
      type: record.type,
      name: record.name,
      content: record.content,
      ttl: record.ttl === 'auto' ? 1 : record.ttl,
      priority: record.priority,
      proxied: record.proxied,
    };
    for (const key of Object.keys(params)) {
      if (params[key] === undefined) delete params[key];
    }
    return params;
  }

  /**
   * List all zones available to the configured account/token.
   *
   * @param signal - optional AbortSignal to cancel the request
   * @returns an array of Zone objects
   */
  async getZones(signal?: AbortSignal): Promise<Zone[]> {
    if (DEBUG) console.debug('getZones');
    this.debugRequest('/zones');
    const zones: Zone[] = [];
    for await (const zone of this.client.zones.list({}, { signal })) {
      zones.push(zone as Zone);
    }
    this.debugResponse(zones);
    return zones;
  }

  /**
   * List DNS records for a specific zone.
   *
   * @param zoneId - the id of the zone to list records for
   * @param signal - optional AbortSignal to cancel the request
   * @returns a list of DNSRecord objects
   */
  async getDNSRecords(zoneId: string, page?: number, perPage?: number, signal?: AbortSignal): Promise<DNSRecord[]> {
    if (DEBUG) console.debug('getDNSRecords', { zoneId });
    this.debugRequest(`/zones/${zoneId}/dns_records`);
    const records: DNSRecord[] = [];
    const listParams: any = { zone_id: zoneId };
    if (page) listParams.page = page;
    if (perPage) listParams.per_page = perPage;
    for await (const record of this.client.dns.records.list(listParams as any, { signal })) {
      records.push(record as any as DNSRecord);
    }
    this.debugResponse(records);
    return records;
  }

  /**
   * Create a new DNS record in the specified zone.
   *
   * @param zoneId - the Cloudflare zone id
   * @param record - partial DNS record object with required fields
   * @param signal - optional AbortSignal to cancel the request
   * @returns the created DNSRecord
   */
  async createDNSRecord(zoneId: string, record: Partial<DNSRecord>, signal?: AbortSignal): Promise<DNSRecord> {
    if (DEBUG) console.debug('createDNSRecord', { zoneId, record });
    this.debugRequest(`/zones/${zoneId}/dns_records`, {
      method: 'POST',
      body: record,
    });
    const params = this.buildRecordParams(zoneId, record);
    const result = (await this.client.dns.records.create(params as any, { signal })) as any as DNSRecord;
    this.debugResponse(result);
    return result;
  }

  /**
   * Update an existing DNS record.
   *
   * @param zoneId - zone containing the record
   * @param recordId - the id of the DNS record to update
   * @param record - fields to update
   * @param signal - optional AbortSignal
   * @returns the updated DNSRecord
   */
  async updateDNSRecord(zoneId: string, recordId: string, record: Partial<DNSRecord>, signal?: AbortSignal): Promise<DNSRecord> {
    if (DEBUG) console.debug('updateDNSRecord', { zoneId, recordId, record });
    this.debugRequest(`/zones/${zoneId}/dns_records/${recordId}`, {
      method: 'PUT',
      body: record,
    });
    const params = this.buildRecordParams(zoneId, record);
    const result = (await this.client.dns.records.update(recordId, params as any, { signal })) as any as DNSRecord;
    this.debugResponse(result);
    return result;
  }

  /**
   * Delete a DNS record.
   *
   * @param zoneId - zone containing the record
   * @param recordId - the id of the DNS record to delete
   * @param signal - optional AbortSignal
   */
  async deleteDNSRecord(zoneId: string, recordId: string, signal?: AbortSignal): Promise<void> {
    if (DEBUG) console.debug('deleteDNSRecord', { zoneId, recordId });
    this.debugRequest(`/zones/${zoneId}/dns_records/${recordId}`, { method: 'DELETE' });
    await this.client.dns.records.delete(recordId, { zone_id: zoneId }, { signal });
    this.debugResponse({ deleted: recordId });
  }

  /**
   * Verify a provided token or key by calling the token verification endpoint.
   *
   * This method can be used to check whether the provided credentials are
   * valid for subsequent requests.
   *
   * @param signal - optional AbortSignal
   */
  async verifyToken(signal?: AbortSignal): Promise<void> {
    if (DEBUG) console.debug('verifyToken');
    this.debugRequest('/user/tokens/verify');
    try {
      // Try token verification endpoint; if it fails (e.g. using a global
      // key with email) fall back to a lightweight call that validates
      // credentials such as listing zones.
      await this.client.user.tokens.verify({ signal });
    } catch (err) {
      this.debugResponse({ verified: false, error: (err as Error).message });
      // fallback: try to list zones with a single page to validate creds
      await this.client.zones.list({ page: 1, per_page: 1 }, { signal });
    }
    this.debugResponse({ verified: true });
  }
}

import type { Request, Response, NextFunction } from 'express';
import { getEnv } from './env';
import createCredentialStore from './credential-store';

/**
 * Simple RBAC middleware: allow when x-admin-token matches env ADMIN_TOKEN or
 * when the request contains a header `x-auth-email` that corresponds to a
 * user row with role "admin" in the sqlite user table. This is intentionally
 * lightweight - for production consider using a robust identity system.
 */
export async function isAdmin(req: Request, res: Response, next: NextFunction) {
  const adminToken = getEnv('ADMIN_TOKEN', 'VITE_ADMIN_TOKEN', undefined);
  const reqAdmin = req.header('x-admin-token');
  if (reqAdmin && adminToken && reqAdmin === adminToken) return next();

  // fallback to check email mapping in sqlite (if credential store supports DB)
  const email = req.header('x-auth-email');
  if (!email) {
    res.status(403).json({ error: 'Admin credentials required' });
    return;
  }
  const store: any = createCredentialStore();
  // if store exposes a db (sqlite store), check users table
  if (store && (store as any).db) {
    try {
      const db: any = (store as any).db;
      await db.run('CREATE TABLE IF NOT EXISTS users (id TEXT PRIMARY KEY, email TEXT, roles TEXT)');
      const row = await db.get('SELECT roles FROM users WHERE email = ?', [email]);
      if (!row) {
        res.status(403).json({ error: 'Admin credentials required' });
        return;
      }
      const roles = JSON.parse(row.roles || '[]');
      if (Array.isArray(roles) && roles.includes('admin')) {
        return next();
      }
    } catch (e) {
      // fallback to deny
      res.status(403).json({ error: 'Admin credentials required' });
      return;
    }
  }
  res.status(403).json({ error: 'Admin credentials required' });
}

export default { isAdmin };

import assert from 'node:assert/strict';
import { test } from 'node:test';
import { ServerAPI } from '../src/lib/server-api.ts';
import { isAdmin } from '../src/lib/rbac';
import createCredentialStore from '../src/lib/credential-store.ts';

function makeReq(body: any, params: any, headers?: Record<string, string>) {
  return {
    body,
    params,
    header(name: string) {
      return (headers && headers[name]) ?? undefined;
    },
  } as any;
}

function makeRes() {
  let statusCode: number | undefined;
  let jsonData: any;
  const res: any = {
    status(code: number) { statusCode = code; return this; },
    json(data: any) { jsonData = data; },
  };
  return { res, get status() { return statusCode; }, get data() { return jsonData; } } as any;
}

test('RBAC: admin token or user role is required for admin endpoints', async () => {
  process.env.CREDENTIAL_STORE = 'sqlite';
  process.env.ADMIN_TOKEN = 'adm-token';
  const store = createCredentialStore() as any;
  ServerAPI.setCredentialStore(store);

  // Create user via admin token
  const reqCreate = makeReq({ id: 'u1', email: 'admin@example.com', roles: ['admin'] }, {}, { 'x-admin-token': 'adm-token' });
  const createdRes = makeRes();
  await ServerAPI.createUser()(reqCreate as any, createdRes.res);
  assert.equal(createdRes.data.success, true);

  // Now try to access audit without credentials -> 403
  const arReq = makeReq({}, {}, {});
  const arRes = makeRes();
  try {
    await ServerAPI.getAuditEntries()(arReq as any, arRes.res);
    // Without admin token or Cloudflare creds, either 400 or 403 is acceptable
    assert.ok(arRes.status === 400 || arRes.status === 403);
  } catch (err: any) {
    assert.equal(err.status, 400);
  }

  // With the admin token it works
  const arReq2 = makeReq({}, {}, { 'x-admin-token': 'adm-token' });
  const arRes2 = makeRes();
  console.log('DEBUG adminToken env=', process.env.ADMIN_TOKEN, 'req header=', arReq2.header('x-admin-token'));
  process.env.DEBUG_SERVER_API = '1';
  await ServerAPI.getAuditEntries()(arReq2 as any, arRes2.res);
  assert.ok(Array.isArray(arRes2.data));

  // Check role-based admin via middleware: isAdmin should allow our admin user
  const mwReq = makeReq({}, {}, { 'x-auth-email': 'admin@example.com' });
  const mwRes = makeRes();
  let called = false;
  await new Promise<void>((resolve, reject) => {
    isAdmin(mwReq as any, mwRes.res, (err?: any) => {
      if (err) {
        reject(err);
      } else {
        called = true;
        resolve();
      }
    });
  });
  assert.equal(called, true);
});

import { test } from 'node:test';
import assert from 'node:assert/strict';
import { ServerClient } from '../src/lib/server-client';

test('ServerClient simulateSPF and getSPFGraph', async () => {
  const sc = new ServerClient('', 'http://localhost:8787/api', undefined, 5000);
  const originalFetch = (global as any).fetch;
  const fakeFetch: any = async (url: string) => {
    if (url.includes('/api/spf/simulate')) {
      return new Response(JSON.stringify({ result: 'pass', reasons: ['matched ip4'], lookups: 1 }), { status: 200, headers: { 'Content-Type': 'application/json' } });
    }
    if (url.includes('/api/spf/graph')) {
      return new Response(JSON.stringify({ nodes: [{ domain: 'example.com' }], edges: [], lookups: 1 }), { status: 200, headers: { 'Content-Type': 'application/json' } });
    }
    return new Response(null, { status: 404 });
  };
  (global as any).fetch = fakeFetch;
  try {
    const sim = await sc.simulateSPF('example.com', '1.2.3.4');
    assert.equal(sim.result, 'pass');
    const graph = await sc.getSPFGraph('example.com');
    assert.ok((graph as any).nodes && (graph as any).nodes.length === 1);
  } finally {
    (global as any).fetch = originalFetch;
  }
});

